import os

DIR_OUTPUT = "DJ_OUTPUT"

def write_file(str, filename):
    dir = os.path.join(os.getcwd(), DIR_OUTPUT)    # 在当前目录下 构建DIR_OUTPUT目录
    os.makedirs(dir, exist_ok=True)  # exist_ok=True 如果目录已经存在不报错
    file_fullpath = os.path.join(dir, filename)
    print(f"\n\n =============={file_fullpath}============ \n\n")
    with open(file_fullpath, 'w', encoding='utf-8') as fw:
        fw.write(str)


